<template> <div class="column"> <div class="ui blue segment"> <div class="ui statistic"> <div class="value"> <i class="blue bell icon"></i><br>{{MaintenanceReportsData.length+reqPremReportData.length+reqVipReportData.length}}</div><div class="label"> Important Reports </div></div></div></div></template><script>export default{data(){return{MaintenanceReportsData: [], reqPremReportData: [], reqVipReportData: []}}, methods:{reports(){var $this=this this.$db.collection('maintenance') .onSnapshot(function(querySnapshot){$this.MaintenanceReportsData=[] querySnapshot.forEach(function(doc){$this.MaintenanceReportsData.push(doc.id)})}) this.$db.collection('requestPremium') .onSnapshot(function(querySnapshot){$this.reqPremReportData=[] querySnapshot.forEach(function(doc){$this.reqPremReportData.push(doc.id)})}) this.$db.collection('requestPremium') .onSnapshot(function(querySnapshot){$this.reqVipReportData=[] querySnapshot.forEach(function(doc){$this.reqVipReportData.push(doc.id)})})},}, mounted(){this.reports()}}</script>