<template> <div class="column"> <div class="ui teal segment"> <div class="ui statistic"> <div class="value"> <i class="teal user icon"></i><br><!-- <div class="ui placeholder"> <div class="line"></div></div>-->{{users.length}}</div><div class="label">Registered Users Today</div></div></div></div></template><script>export default{data: function(){return{users: [],}}, methods:{registeredUsers(){var $this=this this.$db.collection('commuter') .onSnapshot(function(querySnapshot){$this.users=[] querySnapshot.forEach(function(doc){$this.users.push(doc.id)})})},}, mounted(){this.registeredUsers();}}</script>