<template> <div class="column"> <div class="ui violet segment"> <div class="ui statistic"> <div class="value"> <i class="violet bus icon"></i><br>{{unitsRunning.length}}</div><div class="label"> Units Running </div></div></div></div></template><script>export default{data: function(){return{unitsRunning: [],}}, methods:{beepsRunning(){var $this=this this.$db.collection('busUnit') .where('status','==','Running') .onSnapshot(function(querySnapshot){$this.unitsRunning=[] querySnapshot.forEach(function(doc){$this.unitsRunning.push(doc.id)})})},}, mounted(){this.beepsRunning()}}</script>