<template> <div class="ui mini modal" id="delBeepModal"> <div class="content"> <h3 style="text-align:center">Are you sure you want to delete this admin?</h3> <div class="ui divider"></div><div class="ui two column grid"> <div class="column"> <button class="ui red fluid button" type="button" @click="confirm()" :class="loadingButton?'loading disabled':''">Delete</button> </div><div class="column"> <button class="ui primary fluid button" type="button" @click="cancel()" :class="loadingButton?'loading disabled':''">Nevermind</button> </div></div></div></div></template><script>import $ from 'jquery';window.$=window.jQuery=$;export default{props:{beepID:{}}, data(){return{loadingButton: false,}}, methods:{confirm(){var $this=this this.loadingButton=true this.$db.collection('busUnit') .doc($this.beepID.beepid) .update({status: 'Suspend'}) .then(function(){$('#delBeepModal').modal('hide') $this.loadingButton=false})}, cancel(){$('#delBeepModal').modal('hide')}}}</script>